package com.springqprobackend.springqpro.controller;

import com.springqprobackend.springqpro.domain.TaskEntity;
import com.springqprobackend.springqpro.service.QueueService;
import com.springqprobackend.springqpro.models.Task;
import com.springqprobackend.springqpro.enums.TaskStatus;
import com.springqprobackend.springqpro.enums.TaskType;
import com.springqprobackend.springqpro.service.TaskService;
import jakarta.validation.Valid;
import jakarta.validation.constraints.NotBlank;
import jakarta.validation.constraints.NotNull;
import jakarta.validation.constraints.Size;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.time.Instant;
import java.util.Arrays;
import java.util.Map;
import java.util.List;
import java.time.LocalDateTime;
import java.util.stream.Collectors;

@RestController
@RequestMapping("/api")
//@CrossOrigin(origins = "${CORS_ALLOWED_ORIGIN}") // for Netlify/Railway CORS (also local dev) <-- this line alone should replace the CORS stuff I had in Producer.go
public class ProducerController {
    // Field(s):
    private static final Logger logger = LoggerFactory.getLogger(ProducerController.class);
    private final QueueService queue;
    private final TaskService taskService;
    /* NOTE: Don't need @Autowired annotation here. Explicit @Autowired is only needed for multiple constructors or setter-based injection.
    -- Spring will automatically inject constructor parameters for single constructors.
    -- Regarding this.queue = queue;, see comment in Worker.java class (Java is pass-by-value for objects, but that value is the reference). */
    public ProducerController(QueueService queue, TaskService taskService) {
        this.queue = queue;
        this.taskService = taskService;
    }

    // 0. My GoQueue project had a struct "type EnqueueReq struct {...}" in its producer.go file. This would be the equivalent:
    // -- This is the DOT (Data Transfer Object).
    public static class EnqueueReq {
        @NotBlank(message="Payload cannot be blank.")
        @Size(max = 1000, message="Payload should not exceed 1000 characters.")
        public String payload;
        @NotNull(message="Task Type cannot be NULL.")
        public TaskType type;
    }

    // 1. The equivalent of GoQueue's "http.HandleFunc("/api/enqueue", func(w http.ResponseWriter, r *http.Request) {...}" function:
    /* TO-DO:+NOTE: Should probably also make one where a Task itself is directly instantiated since I added Validation Annotations to that file...
    -- I guess the problem is that the EnqueueReq one is already mapped to /enqueue? Or can I add another one for it?
    -- Not super high priority right now. Come back to this later and figure something out...
    * */
    @PostMapping("/enqueue")
    public ResponseEntity<Map<String, String>> handleEnqueue(@Valid @RequestBody EnqueueReq req) {
        if(req.type == null) {
            return ResponseEntity.badRequest().body(Map.of("error", "Task type is required."));
        }
        TaskEntity entity = taskService.createTask(req.payload, req.type);
        return ResponseEntity.ok(Map.of("message", String.format("Job %s (Payload: %s, Type: %s) enqueued! Status: %s", entity.getId(), entity.getPayload(), entity.getType().toString(), entity.getStatus().toString())));
    }

    /* 2025-11-17-DEBUG: JUST SOMETHING TO UNDERSTAND -- EFFECTIVELY EVERYTHING BENEATH HERE IS LEGACY CODE.
    FOR ALL OF THIS RETRIEVAL DATA, WE'RE GOING TO BE WORKING WITH GRAPHQL OR EVEN THE MIRROR REST CONTROLLER
    THAT I HAVE WRITTEN AND DEFINED INSIDE THE SAME DIRECTORY. */
    // EDIT: LIKE I MIGHT AS WELL SWAP IN ALL THE CODE FOR WHAT I HAVE IN TaskRestController's COUNTERPART METHODS.

    // 2. The equivalent of GoQueue's "http.HandleFunc("/api/jobs", func(w http.ResponseWriter, r *http.Request) {...}" function:
    // From producer.go: "THIS IS FOR [GET /api/jobs] and [GET /api/jobs?status=queued]" <-- hence why we're using @RequestParam
    @GetMapping("/jobs")
    public ResponseEntity<List<Task>> handleListJobs(@RequestParam(required = false) String status) {
        //Task[] allJobs = queue.getJobs();
        //List<Task> filtered = Arrays.stream(allJobs).filter(t -> t != null && (status == null || t.getStatus().toString().equalsIgnoreCase(status))).collect(Collectors.toList());
        List<Task> allJobs = queue.getJobs();
        logger.info("The value of allJobs is {} and the value of queue.getJobs() is {}", allJobs, queue.getJobs());
        List<Task> filtered = allJobs.stream().filter(t -> t != null && (status == null || t.getStatus().toString().equalsIgnoreCase(status))).collect(Collectors.toList());
        return ResponseEntity.ok(filtered);
    }

    // The handlers below will be for the individual methods in GoQueue's "http.HandleFunc("/api/jobs/", func(w http.ResponseWriter, r *http.Request) {...}" function:
    // 3. This is for [GET /api/jobs/:id]:
    @GetMapping("/jobs/{id}")
    public ResponseEntity<Task> handleGetJobById(@PathVariable String id) {
        Task t = queue.getJobById(id);
        if(t == null) return ResponseEntity.notFound().build();
        return ResponseEntity.ok(t);
    }

    // 4. This is for [POST /api/jobs/:id/retry]:
    @PostMapping("/jobs/{id}/retry")
    public ResponseEntity<?> handleRetryJobById(@PathVariable String id) {
        Task t = queue.getJobById(id);
        if(t == null) return ResponseEntity.notFound().build();
        if(t.getStatus() != TaskStatus.FAILED) return ResponseEntity.badRequest().body(Map.of("error", "[Retry attempt] Can only retry failed jobs"));

        Task tClone = new Task(
                "Task-" + System.nanoTime(),
                t.getPayload(),
                t.getType(),
                TaskStatus.QUEUED,
                0,
                3,
                Instant.now()
                //LocalDateTime.now()//.format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss"))
        );
        queue.enqueue(tClone);
        return ResponseEntity.ok(tClone);
    }

    // 5. This is for [DELETE /api/jobs/:id]
    @DeleteMapping("/jobs/{id}")
    public ResponseEntity<?> handleDeleteJobById(@PathVariable String id) {
        boolean deleteRes = queue.deleteJob(id);
        if(!deleteRes) return ResponseEntity.notFound().build();
        return ResponseEntity.ok(Map.of("message", String.format("Job %s deleted!", id)));
    }
    /* DEBUG:+NOTE:+TO-DO: ^ When I get to the stage where I start really expanding on the API endpoints (making this a deployable microservice),
    I want to change the return value here slightly. In best practice, it's not supposed to be a 200 (OK) response, RESTful API
    design has it so that what I'd do here is return 204 (No Content) sign, which would imply "the resource was deleted successfully,
    there is no further content to return."
    DEBUG:+NOTE:+TO-DO: Re-scan over all the functions, honestly, and evaluate if my return codes are correct later. (Do some more reading into return codes, etc).
    */

    // 6. This is for the [POST /api/clear]
    @PostMapping("/clear")
    public ResponseEntity<?> clearQueue() {
        queue.clear();
        return ResponseEntity.ok(Map.of("message", "All jobs in the queue cleared!"));
    }

}

package com.springqprobackend.springqpro.domain;

import com.springqprobackend.springqpro.enums.TaskStatus;
import com.springqprobackend.springqpro.enums.TaskType;
import jakarta.persistence.*;

import java.time.Instant;

/* NOTE(S)-TO-SELF:
- @Entity marks this class for persistence (Hibernate will map it to a DataBase table). Each field corresponds to a column.
- ^ will assume same table name, but you can specify with @Table(name="tasks") <-- good practice for table names is all lowercase.
- @Id obv defines the primary key.
- @Enumrated stores enum names (like "QUEUED") instead of integers.
- @Column controls SQL column behavior (type, name).
- Var "Instant" is preferred for timestamps over "LocalDateTime" (UTC-Friendly).
Spring Boot w/ Spring Data JPA, through Hibernate, automatically generates the SQL schema and performs CRUD operations.

Also, this file isn't meant to re-place Task.java (which stands as the runtime / in-memory representation of a Task).
This TaskEntity file here is the persistence representation (database-backed model for durable storage).
*/

/* NOTE(S)-TO-SELF - [PART TWO - 2025-11-13 EDITS]:
- The @Version annotation is a JPA annotation for "optimistic locking" -- a mechanism to prevent concurrent modifications
to the same entity in a database (this is for data integrity in multi-user environments).
- I'm working on Persistence Validation at the moment (making sure that changes to my in-memory Tasks via QueueService
e.g., Task's status goes from QUEUED -> INPROGRESS -> COMPLETED is persisted to the DataBase storage end (this is not yet implemented).
- You put @Version over a Long/Integer field to basically identify the version of the entity it's placed on.

[Explanation on why this is required / the race condition concern in my application wrt persisting data]:
In my project right now, I've got two separate layers of concurrency:
1. Application-level Concurrency (my QueueService in-memory stuff, what I started off with)
- My ExecutorService (worker threads) takes tasks off of the in-memory queue and processes them concurrently.
This part is well-contained and deterministic in memory. (Tasks are mapped to specific Workers for handling, logic is fine).
2. Persistence-level Concurrency (JPA, DB stuff, etc.)
- Basically, there's a possibility that these same threads could try to update and persist TaskEntity rows in the database
and those attempts might overlap in time (multiple threads/workers interacting with the same persistent record at once -- that
is a Persistence Race Condition).

^ Yes, it's not a particularly common concern; the way my system is set up, it's rare that something like this could happen.
My project design has it so that each Task is taken and "owned" (during processing) by one Worker Thread at a time (that's the whole dequeue system).
That makes it so that it's pretty unlikely there will be two threads will modify the same task simultaneously; ExecutorService takes care of most race conditions.
BUT --- IT CAN STILL HAPPEN (particularly externally with tools like Postman, GraphiQL, and so on).

Persistence race conditions can still happen in the event of:
- You have retries or resubmissions that cause overlapping updates for the same Task (identified by TaskID ofc).
- A user action via Postman, GraphiQL, and so on, changes the task as its being processed.
- I eventually add background database syncs or monitoring that touches tasks concurrently.

Handling this with @Version etc is the way that real Job Queue systems like SideKiq, Celery, and so on do it -- so I will too!!!
*/

@Entity
@Table(name="tasks")
public class TaskEntity {
    @Id
    private String id;

    @Column(columnDefinition="text")    // Allows long payloads
    private String payload;

    @Enumerated(EnumType.STRING)    // Store enum as readable string (as opposed to numeric).
    private TaskType type;

    @Enumerated(EnumType.STRING)
    private TaskStatus status;

    private int attempts;
    private int maxRetries;

    @Column(name="created_at")  // custom DataBase column name.
    private Instant createdAt;

    // Optimistic locking field (protects against lost updates):
    @Version
    private Long version;

    // NOTE: Hibernate needs a no-args constructor. Think about using "protected" for all of these in the future tbh.
    protected TaskEntity() {}
    // Constructor:
    public TaskEntity(String id, String payload, TaskType type, TaskStatus status, int attempts, int maxRetries, Instant createdAt) {
        this.id = id;
        this.payload = payload;
        this.type = type;
        this.status = status;
        this.attempts = attempts;
        this.maxRetries = maxRetries;
        this.createdAt = createdAt;
    }

    // JPA will use the getter and setter methods to map to the table columns:
    // getters:
    public String getId() { return id; }
    public String getPayload() { return payload; }
    public TaskType getType() { return type; }
    public TaskStatus getStatus() { return status; }
    public int getAttempts() { return attempts; }
    public int getMaxRetries() { return maxRetries; }
    public Instant getCreatedAt() { return createdAt; }
    public Long getVersion() { return version; }
    // setters:
    public void setId(String id) { this.id = id; }
    public void setPayload(String payload) { this.payload = payload; }
    public void setType(TaskType type) { this.type = type; }
    public void setStatus(TaskStatus status) { this.status = status; }
    public void setAttempts(int attempts) { this.attempts = attempts; }
    public void setMaxRetries(int maxRetries) { this.maxRetries = maxRetries; }
    public void setCreatedAt(Instant createdAt) { this.createdAt = createdAt; }
}

