package com.springqprobackend.springqpro.graphql;

import com.springqprobackend.springqpro.domain.TaskEntity;
import com.springqprobackend.springqpro.enums.TaskStatus;
import com.springqprobackend.springqpro.enums.TaskType;
import com.springqprobackend.springqpro.service.TaskService;
import com.springqprobackend.springqpro.graphql.controllerRecords.CreateTaskInput;
import com.springqprobackend.springqpro.graphql.controllerRecords.UpdateTaskInput;
import org.springframework.stereotype.Controller;
import org.springframework.graphql.data.method.annotation.Argument;
import org.springframework.graphql.data.method.annotation.MutationMapping;
import org.springframework.graphql.data.method.annotation.QueryMapping;
import org.springframework.transaction.annotation.Transactional;

import java.util.List;

/* NOTE(S)-TO-SELF:
- @QueryMapping is the GraphQL query resolver (to read operations / basically retrieve stuff, from what I understand).
- @MutationMapping is the GraphQL mutation resolver (to write operations / to modify data basically).
- @Argument maps GraphQL query args to method parameters.
- The schema defines how the client *sees* the data.
- Spring will automatically wire the /graphql endpoint (no controller mapping is needed).
*/

/* 2025-11-12-DEBUG:
DONE:
- task (query)
- tasks (query)
- createTask (mutation)
TO-DO FROM schema.graphqls:
- updateTask (mutation)
- deleteTask (mutation)
*/

@Controller // IMPORTANT NOTE: GraphQL controllers use @Controller, NOT @RestController (remember this!)
public class TaskGraphQLController {
    // Field(s):
    private final TaskService taskService;
    // Constructor(s):
    public TaskGraphQLController(TaskService taskService) {
        this.taskService = taskService;
    }

    @QueryMapping   // This is GraphQL query resolver.
    public List<TaskEntity> tasks(@Argument TaskStatus status) {
        return taskService.getAllTasks(status);
    }

    @QueryMapping
    public TaskEntity task(@Argument String id) {
        return taskService.getTask(id).orElse(null);
    }

    @MutationMapping
    public TaskEntity createTask(@Argument("input") CreateTaskInput input) {
        return taskService.createTask(input.payload(), input.type());
    }

    @MutationMapping
    @Transactional
    public TaskEntity updateTask(@Argument("input") UpdateTaskInput input) {
        taskService.updateStatus(input.id(), input.status(), input.attempts());
        return task(input.id());    // or "return taskService.getTask(id).orElse(null);"
    }

    @MutationMapping
    @Transactional
    public boolean deleteTask(@Argument String id) {
        return taskService.deleteTask(id);
    }
}

package com.springqprobackend.springqpro.graphql;
/* NOTE: This file is primarily included for the purpose of practice, and also I guess "mirror testing."
This is just a REST Controller that one-to-one mimics the purpose of my GraphQL Controller.
(That's also why I have -- at least currently -- have it in the /graphql package directory).
*/

import com.springqprobackend.springqpro.domain.TaskEntity;
import com.springqprobackend.springqpro.enums.TaskStatus;
import com.springqprobackend.springqpro.service.TaskService;
import com.springqprobackend.springqpro.graphql.controllerRecords.CreateTaskInput;
import com.springqprobackend.springqpro.graphql.controllerRecords.UpdateTaskInput;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.util.List;

@RestController
@RequestMapping("/api/tasks")
public class TaskRestController {
    // Field(s):
    private TaskService taskService;
    // Constructor(s):
    public TaskRestController(TaskService taskService) { this.taskService = taskService; }

    @GetMapping("/jobs")
    public List<TaskEntity> tasks(@RequestParam(required = false) TaskStatus status) {
        return taskService.getAllTasks(status);
    }

    @GetMapping("/jobs/{id}")
    public ResponseEntity<TaskEntity> task(@PathVariable String id) {
        return taskService.getTask(id).map(ResponseEntity::ok).orElse(ResponseEntity.notFound().build());
    }

    @PostMapping("/create")
    public TaskEntity createTask(@RequestBody CreateTaskInput input) {
        return taskService.createTask(input.payload(), input.type());
    }

    @PatchMapping("/update/{id}")
    public ResponseEntity<TaskEntity> updateTask(@PathVariable String id, @RequestBody UpdateTaskInput input) {
        taskService.updateStatus(id, input.status(), input.attempts());
        return taskService.getTask(id).map(ResponseEntity::ok).orElse(ResponseEntity.notFound().build());
    }

    @DeleteMapping("/delete/{id}")
    public ResponseEntity<Void> deleteTask(@PathVariable String id) {
        if (taskService.deleteTask(id)) return ResponseEntity.noContent().build();
        return ResponseEntity.notFound().build();
    }
}

package com.springqprobackend.springqpro.handlers;

import com.springqprobackend.springqpro.config.TaskHandlerProperties;
import com.springqprobackend.springqpro.enums.TaskStatus;
import com.springqprobackend.springqpro.interfaces.Sleeper;
import com.springqprobackend.springqpro.interfaces.TaskHandler;
import com.springqprobackend.springqpro.models.Task;

import com.springqprobackend.springqpro.service.QueueService;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.stereotype.Component;

@Component("DATACLEANUP")
public class DataCleanUpHandler implements TaskHandler {
    private static final Logger logger = LoggerFactory.getLogger(DataCleanUpHandler.class);
    private final Sleeper sleeper;
    private final TaskHandlerProperties props;

    public DataCleanUpHandler(Sleeper sleeper, TaskHandlerProperties props) {
        this.sleeper = sleeper;
        this.props = props;
    }

    @Override
    public void handle(Task task) throws InterruptedException {
        sleeper.sleep(props.getDataCleanUpSleepTime());
        logger.info("Task {} (Type: {}) completed", task.getId(), task.getType());
    }
}

package com.springqprobackend.springqpro.handlers;

import com.springqprobackend.springqpro.config.TaskHandlerProperties;
import com.springqprobackend.springqpro.enums.TaskStatus;
import com.springqprobackend.springqpro.interfaces.Sleeper;
import com.springqprobackend.springqpro.interfaces.TaskHandler;
import com.springqprobackend.springqpro.models.Task;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.stereotype.Component;

@Component("DEFAULT")
public class DefaultHandler implements TaskHandler {
    private static final Logger logger = LoggerFactory.getLogger(DefaultHandler.class);
